import React, { Component } from 'react';


import Header from './components/header';
import Crystals from './components/crystals'

class App extends Component {
  constructor(props) {
    super(props);
    this.generateRandomNumber = this.generateRandomNumber.bind(this);
    this.generateCrystalValues = this.generateCrystalValues.bind(this);
    this.addCrystalValue = this.addCrystalValue.bind(this);


    this.state = {
      randomNumber: 0,
      crystals: [
        { crystal: 'Gold', value: this.generateCrystalValues() },
        { crystal: 'Blue', value: this.generateCrystalValues() },
        { crystal: 'Black', value: this.generateCrystalValues() },
        { crystal: 'Green', value: this.generateCrystalValues() }
      ],
      userTotal: 0,
      wins: 0,
      losses: 0
    }
  }

  componentDidMount() {
    this.setState({
      randomNumber: this.generateRandomNumber()
    })
  }

  generateRandomNumber() {
    return Math.floor(Math.random() * 102) + 19
  }

  generateCrystalValues() {
    return Math.floor(Math.random() * 12) + 1
  }

  addCrystalValue(crystal, value) {
    console.log(`crystal: ${crystal} || value: ${value}`)
  }

  render() {
    return (
      <div>

        <Header />
        <div>
          <h3>
            Random Number: {this.state.randomNumber}
          </h3>
        </div>



        <Crystals addCrystalValue={(crystal, value) => this.addCrystalValue(crystal, value)} crystals={this.state.crystals} />

      </div>
    );
  }
}

export default App;
