import React, { Component } from 'react';
import { ListGroup, ListGroupItem } from 'react-bootstrap'

import Header from './components/header';
import Crystals from './components/crystals'

class App extends Component {
  constructor(props) {
    super(props);
    this.generateRandomNumber = this.generateRandomNumber.bind(this);
    this.generateCrystalValues = this.generateCrystalValues.bind(this);
    this.addCrystalValue = this.addCrystalValue.bind(this);
    this.checkingScore = this.checkingScore.bind(this)


    this.state = {
      randomNumber: 0,
      crystals: [
        { crystal: 'Gold', value: this.generateCrystalValues() },
        { crystal: 'Blue', value: this.generateCrystalValues() },
        { crystal: 'Black', value: this.generateCrystalValues() },
        { crystal: 'Green', value: this.generateCrystalValues() }
      ],
      userTotal: 0,
      wins: 0,
      losses: 0
    }
  }

  componentDidMount() {
    this.setState({
      randomNumber: this.generateRandomNumber()
    })
  }

  generateRandomNumber() {
    return Math.floor(Math.random() * 102) + 19
  }

  generateCrystalValues() {
    return Math.floor(Math.random() * 12) + 1
  }

  addCrystalValue(crystal, value) {
    console.log(`crystal: ${crystal} || value: ${value}`)
    this.setState({
      userTotal: this.state.userTotal + value
    }, () => this.checkingScore())
  }

  checkingScore() {
    if (this.state.randomNumber === this.state.userTotal) {
      this.setState({
        wins: this.state.wins++,
        userTotal: 0
      })
      this.generateRandomNumber();
    }else if(this.state.randomNumber < this.state.userTotal){
      this.setState({
        losses: this.state.losses++,
        userTotal: 0
      })
      this.generateRandomNumber();
    }
  }

  render() {
    return (
      <div>

        <Header />


        <ListGroup>
          <ListGroupItem>
            <div>
              <h4>
                Random Number: {this.state.randomNumber}
              </h4>
            </div>
          </ListGroupItem>
          <ListGroupItem>
            <h4>Wins: {this.state.wins}</h4>
          </ListGroupItem>
          <ListGroupItem> <h4>Losses: {this.state.losses}</h4></ListGroupItem>
          <ListGroupItem>
            <h4>
              Total: {this.state.userTotal}
            </h4>
          </ListGroupItem>

        </ListGroup>
        <Crystals addCrystalValue={(crystal, value) => this.addCrystalValue(crystal, value)} crystals={this.state.crystals} />

      </div>
    );
  }
}

export default App;
