import React, { Component } from 'react';


class Crystals extends Component {
    constructor(props) {
        super(props);
        this.showCrystals = this.showCrystals.bind(this);

        this.state = {
            crystals: ['Gold', 'Blue', 'Black', 'Green']
        }
    }

    showCrystals() {
        return (
            this.state.crystals.map(crystal => {
                return (
                    <div>
                        <h3>
                            {crystal}
                        </h3>
                    </div>

                )
            })
        )
    }
    render() {
        return (
            <div>
                {this.showCrystals()}
            </div>
        )
    }
}

export default Crystals;
