import React, { Component } from 'react';


class Crystals extends Component {
    constructor(props) {
        super(props);
        this.showCrystals = this.showCrystals.bind(this);
    }

    showCrystals() {
        return (
            this.props.crystals.map((element, i) => {
                return (
                    <div onClick={this.props.addCrystalValue(element.crystal, element.value)} key={i}>
                        <h3>
                            {element.crystal}: {element.value}
                        </h3>
                    </div>
                )
            })
        )
    }

    render() {
        return (
            <div>
                {this.showCrystals()}
            </div>
        )
    }
}

export default Crystals;
